    //Avg Resolution Time
    'incidentAvgResolutionOperationData': 'index=gd_summary source=incidentavgrestime Country IN ("countryParam") ServiceType IN ("serviceTypeParam") earliest=-30m@m |eval fromDate = strptime("updatedFromDate", "%m/%d/%Y") | eval toDate = strptime("updatedToDate", "%m/%d/%Y")| eval txnDate = strptime(createdDate, "%Y/%m/%d") | where txnDate <= toDate and  txnDate >= fromDate | eval ACTUALSTARTs= if(isnull(ActualStart),CREATIONDATE,ActualStart) |  eval ACTUALFINISHs= if(isnull(ActualFinish),ChangeDate,ActualFinish) | eval startDate=strptime(ACTUALSTARTs, "%Y-%m-%d %H:%M:%S")  | eval endDate=strptime(ACTUALFINISHs, "%Y-%m-%d %H:%M:%S") | eval dayDiff = (endDate - startDate)/86400 | eventstats count(SourceSystem) as TotalIncidents,sum(dayDiff) as dayDiff  by SourceSystem,txnDate  | eval avg = (dayDiff/TotalIncidents)| stats values(TotalIncidents) as TotalIncidents, values(avg) as AvgTime by SourceSystem,txnDate  | sort -TotalIncidents, -txnDate  |eval LastOccurence = strftime(txnDate, "%m/%d/%Y") |fillnull| table  LastOccurence,SourceSystem, TotalIncidents, AvgTime'
